settings.custom_code:
{% if product.metafields.custom.variation_products.value != blank%}
<div class="cc-custom-variation-header">Select Device</div>
<select class="cc-custom-variation">
{% for item in product.metafields.custom.variation_products.value %}
  {% assign ccselected = '' %}
  {% if item.metafields.custom.variation_value.value != blank%}
  	{% if item.title == product.title %}
      {% assign ccselected = 'selected' %}
  	{% endif %}
    <option {{ ccselected }} data-url="{{ item.url }}">{{ item.metafields.custom.variation_value.value }}</option>
  {% endif %}
{% endfor %}
</select>

<script>
const ccCustomVariations = document.querySelectorAll('.cc-custom-variation');
ccCustomVariations.forEach((ccCustomVariation) => {
    ccCustomVariation.addEventListener('change', function() {
        const selectedOption = this.options[this.selectedIndex];
        location.href = selectedOption.getAttribute('data-url');
    });
});
</script>
<style>
.cc-custom-variation-header { 
  font-size: calc(.875rem * var(--adjust-body)); 
  margin-bottom: 8px; 
}
.cc-custom-variation { 
  -webkit-appearance: none;
  -moz-appearance: none;
  background: var(--input-bg); 
  background-image: url(data:image/svg+xml;charset=UTF-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%2212%22%20height%3D%2212%22%20viewBox%3D%220%200%2012%2012%22%3E%3Ctitle%3Edown-arrow%3C%2Ftitle%3E%3Cg%20fill%3D%22%23000000%22%3E%3Cpath%20d%3D%22M10.293%2C3.293%2C6%2C7.586%2C1.707%2C3.293A1%2C1%2C0%2C0%2C0%2C.293%2C4.707l5%2C5a1%2C1%2C0%2C0%2C0%2C1.414%2C0l5-5a1%2C1%2C0%2C1%2C0-1.414-1.414Z%22%20fill%3D%22%23727272%22%3E%3C%2Fpath%3E%3C%2Fg%3E%3C%2Fsvg%3E);
  background-position: top 50% right 10%;
  background-repeat: no-repeat;
  background-size: 0.6rem;
  border: var(--BORDER-WIDTH) solid var(--text); 
  border-radius: var(--RADIUS); 
  color: var(--text); 
  display: block; 
  font-size: calc(.875rem * var(--adjust-body)); 
  font-family: var(--FONT-STACK-BODY); 
  line-height: 1;
  padding: 13px 40px 13px 20px;
}
@media only screen and (max-width: 767px) {
  .cc-custom-variation-header { 
    font-size: calc(.75rem * var(--adjust-body)); 
  }
}
</style>
{% endif %}
